class Dfp_Datafeed_File_Format_Csv_Dialect_TabsTest extends PHPUnit_Framework_TestCase
{
    public function testGetDelimiter()
    {
        $sut = new Dfp_Datafeed_File_Format_Csv_Dialect_Tabs();
        $this->assertEquals("\t", $sut->getDelimiter());
    }

    public function testGetEscape()
    {
        $sut = new Dfp_Datafeed_File_Format_Csv_Dialect_Tabs();
        $this->assertEquals('\\', $sut->getEscape());
    }

    public function testGetQuote()
    {
        $sut = new Dfp_Datafeed_File_Format_Csv_Dialect_Tabs();
        $this->assertEquals('"', $sut->getQuote());
    }

    public function testhasHeader()
    {
        $sut = new Dfp_Datafeed_File_Format_Csv_Dialect_Tabs();
        $this->assertNull($sut->hasHeader());
    }
}
